        public void AddToCart(int id)
        {
            bool itemInCart = false;
            foreach (var item in Cart)
            {
                if (items.ElementAt(id) == item)
                {
                    itemInCart = true;
                }
            }
            if (itemInCart == false)
            {
                items[id].ItemsTaken += 1;
                Cart.Add(items.ElementAt(id));
                
            }
            else
            {
                items[id].ItemsTaken += 1;
            }
            
        }
