        public IActionResult OpenCart()
        {
            if (CartEntered == false)
            {
               
                foreach (var item in Cart)
                {
                    costs += (item.Price * item.ItemsTaken);
                }

                CartEntered = true;
            }
            return View("Cart", Cart);
        }

        [HttpPost]

        public void AddCostsToBill(int id)
        {
            costs += (items.ElementAt(id).Price)* items.ElementAt(id).ItemsTaken;
            items.ElementAt(id).Checked = true;
        }

        [HttpPost]
        public void SubstractCostsFromBill(int id)
        {
            costs -= (items.ElementAt(id).Price) * items.ElementAt(id).ItemsTaken;
            items.ElementAt(id).Checked = false;
        }

        public ActionResult DeleteItem(int id)
        {

           
            Cart.Remove(items[id]);
            costs -= items.ElementAt(id).Price*items.ElementAt(id).ItemsTaken;
            if (Cart.Count() == 0)
            {
                CartEntered = false;
            }
            return View("Cart", Cart);
        }
