        public IActionResult SearchForItem(string searchTerm, string categories)
        {
            ItemDAO itemDAO = new ItemDAO();
            List<int> temporaryPlaceholderOfListIndexes = new List<int>();
            List<ItemModel> itemsSearched = itemDAO.Search(searchTerm);
            for (int i = 0; i < itemsSearched.Count(); i++)
            {
                if (itemsSearched[i].Category != categories)
                {
                    temporaryPlaceholderOfListIndexes.Add(i);
                }
            }
            for (int i = temporaryPlaceholderOfListIndexes.Count() - 1; i >= 0; i--)
            {
                itemsSearched.Remove(itemsSearched[temporaryPlaceholderOfListIndexes[i]]);
            }
           
            return View("Shop", itemsSearched);
        }
        public IActionResult SearchForItemByCategory(string searchTerm)
        {
            ItemDAO itemDAO = new ItemDAO();
            List<ItemModel> itemsSearched = itemDAO.SearchByCategory(searchTerm);
            return View("Shop", itemsSearched);
        }



