        public void IncrementNumberOfItemsTaken(int id,int maxVal, bool whileInCart)
        {
            
            if (items[id].ItemsTaken < maxVal && whileInCart == true)
            {
                items[id].ItemsTaken++;
                costs += items[id].Price;
            }
            else if (items[id].ItemsTaken < maxVal)
            {
                items[id].ItemsTaken++;

            }



        }
        public void SubstractFromNumberOfItemsTaken(int id, bool whileInCart)
        {

            if (items[id].ItemsTaken > 0 && whileInCart == true)
            {
                items[id].ItemsTaken--;
                costs -= items[id].Price;
            }
            else if(items[id].ItemsTaken > 0)
            {
                items[id].ItemsTaken--;
               
            }



        }

        
