        public ActionResult DetectInputtedValue(string value, string id)
        {
            int Value = int.Parse(value);
            int Id = int.Parse(id);
            items[Id].ItemsTaken = Value;
            return PartialView("_ItemsTakenLabel", items[Id]);

        }
        public ActionResult DetectInputtedValueInCart(string value, string id)
        {

            
            int Value = int.Parse(value);
            int Id = int.Parse(id);
            var saveValue = items[Id].ItemsTaken;
            costs -= (saveValue * items[Id].Price);
            items[Id].ItemsTaken = Value;
            costs += (items[Id].ItemsTaken * items[Id].Price);
            return PartialView("_ItemsTakenLabel", items[Id]);

        }
